        }

        let dt = delta / 1000;

        this.step(dt);
        this.render(dt);
    }

    /**
     * Start timer
     */
    start () {
        if (this.stopped) {
            this.stopped = false;
            requestAnimationFrame(this.loop.bind(this));
        }
    }

    /**
     * Stop timer
     */
    stop () {
        this.stopped = true;
    }

    /**
     * Called at every frame
     * @private
     * @param {number} dt
     * @emits {step}
     */
    step (dt) {
        this.dispatch('step', dt);
    }

    /**
     * Called at every frame
     * @private
     * @param {number} dt
     * @emits {render}
     */
    render (dt) {
        this.dispatch('render', dt);
    }
