export class Collection {
    constructor() {
        this.items = [];
    }

    push(item) {
        return this.items.push(item);
    }

    filter(callback) {
        return this.items.filter(callback);
    }

    find(callback) {
        return this.items.find(callback);
    }

    sort(callback) {
        return this.items.sort(callback);
    }

    forEach(callback) {
        return this.items.forEach(callback);
    }

    remove(callback) {
        const index = this.items.findIndex(callback);

        if (index > -1) {
            return this.items.splice(index, 1);
        }

        return false;
    }

    clear() {
        this.items = [];
    }
}

import { Model } from './../model';
import { Collection } from './collection';

export class CollisionChecker extends Collection {
    /**
     * @param {Model} model
     * @param {Array<Model>|Model} platforms
     * @param {Callable} [event=function()]
     */
    add(model, platforms, event = () => {}) {
        if (!Array.isArray(platforms)) {
            platforms = [platforms];
        }

        this.push({ model, platforms, event });
    }

    /**
     * @param {number} dt
     */
    check(dt) {
        let model, platform;
        this.items.forEach(pair => {
            model = Object.create(pair.model);
            model.step(dt);
            platform = CollisionChecker.hasCollisions(model, pair.platforms);

            if (!!platform) {
                pair.event(pair.model, platform);
            }
        });
    }

    /**
     * @param {Callable} callback
     */
    remove(callback) {
        const index = this.items.findIndex(item => callback(item.model));

        if (index > -1) {
            return this.items.splice(index, 1);
        }

        return false;
    }

    /**
     * Check if there are any collisions between model and array of models
     * @param {Model} model
     * @param {Array<Model>|Model} models
     * @return {boolean}
     */
    static hasCollisions(model, models) {
        if (!model instanceof Model) {
            throw new Error(`Parameter model has to be an instance of Model, it's an instance of ${typeof model} instead.`);
        }

        if (!Array.isArray(models)) {
            models = [models];
        }

        let m;
        for (let i = 0; i < models.length; i++) {
            m = models[i];
            if (!model instanceof Model) {
                throw new Error(`Parameter from models[${i}] has to be an instance of Model, it's an instance of ${typeof m} instead.`);
            }

            if (
                !!model.hitbox.radius &&
                !!m.hitbox.radius &&
                CollisionChecker.hasCollisionBetweenCircleAndCircle(model.hitbox, m.hitbox)
            ) {
                return m;
            } else if (
                !!model.hitbox.radius &&
                !m.hitbox.radius &&
                CollisionChecker.hasCollisionBetweenCircleAndRectangle(model.hitbox, m.hitbox)
            ) {
                return m;
            } else if (
                !model.hitbox.radius &&
                !!m.hitbox.radius &&
                CollisionChecker.hasCollisionBetweenCircleAndRectangle(m.hitbox, model.hitbox)
            ) {
                return m;
            } else if (CollisionChecker.hasCollisionBetweenRectangleAndRectangle(model.hitbox, m.hitbox)) {
                return m;
            }
        }

        return false;
    }

    /**
     * Check collision between circle and another circle
     * @param {Object} circleA
     * @param {number} circleA.x
     * @param {number} circleA.y
     * @param {number} circleA.radius
     * @param {Object} circleB
     * @param {number} circleB.x
     * @param {number} circleB.y
     * @param {number} circleB.radius
     *
     * @return {boolean}
     */
    static hasCollisionBetweenCircleAndCircle(circleA, circleB) {
        const dx = circleA.x - circleB.x;
        const dy = circleA.y - circleB.y;
        const distance = Math.sqrt(dx * dx + dy * dy);

        if (distance < circleA.radius + circleB.radius) {
            return true;
        }

        return false;
    }

    /**
     * Check collision between circle and rectangle
     * @param {Object} circle
     * @param {number} circle.x
     * @param {number} circle.y
     * @param {number} circle.radius
     * @param {Object} rectangle
     * @param {number} rectangle.x
     * @param {number} rectangle.y
     * @param {number} rectangle.width
     * @param {number} rectangle.height
     *
     * @return {boolean}
     */
    static hasCollisionBetweenCircleAndRectangle(circle, rectangle) {
        const distX = Math.abs(circle.x - rectangle.x - rectangle.width / 2);
        const distY = Math.abs(circle.y - rectangle.y - rectangle.height / 2);

        if (distX > rectangle.width / 2 + circle.radius) {
            return false;
        }

        if (distY > rectangle.height / 2 + circle.radius) {
            return false;
        }

        if (distX <= rectangle.width / 2) {
            return true;
        }

        if (distY <= rectangle.height / 2) {
            return true;
        }

        var dx = distX - rectangle.width / 2;
        var dy = distY - rectangle.height / 2;

        return dx * dx + dy * dy <= (circle.radius * circle.radius);
    }

    /**
     * Check collision between rectangle and rectangle
     * @param {Object} rectangleA
     * @param {number} rectangleA.x
     * @param {number} rectangleA.y
     * @param {number} rectangleA.width
     * @param {number} rectangleA.height
     * @param {Object} rectangleB
     * @param {number} rectangleB.x
     * @param {number} rectangleB.y
     * @param {number} rectangleB.width
     * @param {number} rectangleB.height
     *
     * @return {boolean}
     */
    static hasCollisionBetweenRectangleAndRectangle(rectangleA, rectangleB) {
        if (rectangleA.x < rectangleB.x + rectangleB.width &&
            rectangleA.x + rectangleA.width > rectangleB.x &&
            rectangleA.y < rectangleB.y + rectangleB.height &&
            rectangleA.y + rectangleA.height > rectangleB.y
        ) {
            return true;
        }

        return false;
    }
}

import { Model } from './../model';
import { Drawer } from './../drawer';
import { AStar } from './pathfinds/a-star';

/**
 * @ignore
 */
export class Map {
    constructor(tileWidth, tileHeight, columns, rows) {
        this.tileWidth = tileWidth;
        this.tileHeight = tileHeight;
        this.columns = columns;
        this.rows = rows;
        this.defaultTileset = null;
        this.layers = {};
        this.tilesets = {};
        this.platforms = {};
        this.pathFindingLayer = {};

        this.width = tileWidth * columns;
        this.height = tileHeight * rows;
    }

    addLayer(layer, name, z = 0, tileset = null) {
        const tiles = layer.data || layer.tiles || [];
        const rows = layer.height || layer.rows || this.rows;
        const columns = layer.width || layer.columns || this.columns;
        z = z || layer.z || 0;

        this.layers[name] = { tiles, rows, columns, name, tileset, z };
    }

    addTileset(tileset, name, isDefault = true) {
        this.tilesets[name] = tileset;

        if (isDefault) {
            this.defaultTileset = tileset;
        }
    }

    addPlatformLayer(layer, name, z = 0, hidden = true) {
        const tiles = layer.data || layer.tiles || [];
        const columns = layer.width || layer.columns || this.columns;
        const rows = layer.height || layer.rows || this.rows;

        this.platforms[name] = [];

        let t = 0;
        for (let r = 0; r < rows; r++) {
            for (let c = 0; c < columns; c++) {
                if (0 !== tiles[t]) {
                    this.platforms[name].push(
                        new Model(
                            c * this.tileWidth,
                            r * this.tileHeight,
                            this.tileWidth,
                            this.tileHeight
                        )
                    );
                }
                t++;
            }
        }

        if (!hidden) {
            this.addLayer(layer, name, z);
        }
    }

    addPathFindingLayer(layer, name) {
        const tiles = layer.data || layer.tiles || [];
        const rows = layer.height || layer.rows || this.rows;
        const columns = layer.width || layer.columns || this.columns;
        const nodes = [];

        let x, y;
        let t = 0;
        for (let c = 0; c < columns; c++) {
            for (let r = 0; r < rows; r++) {
                x = r * this.tileWidth;
                y = c * this.tileHeight;

                nodes.push({
                    x, y,
                    posX: r, posY: c,
                    walkable: 0 === tiles[t],
                    g: 0, h: 0
                });

                t++;
            }
        }

        this.pathFindingLayer[name] = { nodes, rows, columns, name };
    }

    getLayer(name) {
        return this.layers[name] || null;
    }

    getPlatform(name) {
        return this.platforms[name] || [];
    }

    getTileset(name = null) {
        return this.tilesets[name] || null;
    }

    findPathBetweenPositions(name, objectA, objectB) {
        const layer = this.pathFindingLayer[name];
        const finder = new AStar();

        if (!!layer) {
            const startPosX = Math.floor(objectA.x / this.tileWidth);
            const startPosY = Math.floor(objectA.y / this.tileHeight);

            const endPosX = Math.floor(objectB.x / this.tileWidth);
            const endPosY = Math.floor(objectB.y / this.tileHeight);

            const startNode = { posX: startPosX, posY: startPosY, f: 0, g: 0, h: 0 };
            const endNode = { posX: endPosX, posY: endPosY, f: 0, g: 0, h: 0 };

            return finder.findPath(layer.nodes, startNode, endNode);
        }

        return [];
    }

    render(drawer, options) {
        if (!drawer instanceof Drawer) {
            throw new Error(`Parameter drawer has to be an instance of Drawer, it's an instance of ${typeof drawer} instead.`);
        }

        const ctx = drawer.ctx;
        const minZ = options.minZ || 0;
        const maxZ = options.maxZ || 999;
        const limits = options.limits || { x: 0, y: 0, width: 9999, height: 9999 };
        const onlyLayer = options.layer || null;

        let tileset = null;
        let layer = null;
        let layers = this.layers;

        for (let layerName in this.layers) {
            layer = this.layers[layerName];

            if (layer.z < minZ || layer.z > maxZ || (null !== onlyLayer && onlyLayer !== layerName)) {
                continue;
            }

            tileset = null !== layer.tileset ? this.getTileset(layer.tileset) : this.defaultTileset;

            let t = 0;
            for (let columns = 0; columns < layer.columns; columns++) {
                for (let rows = 0; rows < layer.rows; rows++) {
                    tileset.x = rows * this.tileWidth;
                    tileset.y = columns * this.tileHeight;

                    // Check if tile is into limits
                    if (
                        tileset.x + this.tileWidth >= limits.x &&
                        tileset.x <= limits.x + limits.width &&
                        tileset.y + this.tileHeight >= limits.y &&
                        tileset.y <= limits.y + limits.height
                    ) {
                        tileset.renderTile(layer.tiles[t] || 0, ctx);
                    }

                    t++;
                }
            }
        }
    }
}

/**
 * @ignore
 */
export class AStar {
    constructor() {
        this.openList = [];
        this.closeList = [];

        this.NODE_DISTANCE_VALUE = 100;
    }

    findPath(nodes, startNode, endNode) {
        const path = [];
        let currentNode = null;
        let neighbours = [];
        let g = 0;
        let h = 0;
        let f = 0;

        this.addToOpenList(startNode);
        while (this.openList.length > 0) {
            currentNode = this.getCurrentNode();
            if (currentNode.posX === endNode.posX && currentNode.posY === endNode.posY) {
                break;
            }

            this.addToCloseList(currentNode);
            this.getNeighbours(currentNode, nodes).forEach(node => {
                if (this.isOnCloseList(node) || !node.walkable) {
                    return;
                }

                g = (!!node.parent ? node.parent.g : node.g) + this.NODE_DISTANCE_VALUE;
                h = (Math.abs(endNode.posX - node.posX) + Math.abs(endNode.posY - node.posY)) * this.NODE_DISTANCE_VALUE;
                f = h + g;

                if (this.isOnOpenList(node)) {
                    if (g < node.g) {
                        node.parent = currentNode;
                        node.g = g;
                        node.h = h;
                        node.f = f;
                    }
                } else {
                    this.addToOpenList(node);
                    node.parent = currentNode;
                    node.g = g;
                    node.h = h;
                    node.f = f;
                }
            });
        }

        if (this.openList.length === 0) {
            return path;
        }

        var lastNode = this.getNode(endNode.posX, endNode.posY, nodes);
        while (lastNode.posX !== startNode.posX || lastNode.posY !== startNode.posY) {
            path.push(lastNode);
            lastNode = lastNode.parent;
        }
        path.push(this.getNode(startNode.posX, startNode.posY, nodes));

        return path.reverse();
    }

    removeFromCloseList(node) {
        const index = this.closeList.findIndex(n => n.posX === node.posX && n.posY === node.posY);

        if (index > -1) {
            this.closeList.splice(index, 1);
        }
    }

    removeFromOpenList(node) {
        const index = this.openList.findIndex(n => n.posX === node.posX && n.posY === node.posY);

        if (index > -1) {
            this.openList.splice(index, 1);
        }
    }

    addToCloseList(node) {
        const hasNode = this.closeList.find(n => n.posX === node.posX && n.posY === node.posY);

        if (!hasNode) {
            this.removeFromOpenList(node);
            this.closeList.push(node);
        }
    }

    addToOpenList(node) {
        const hasNode = this.openList.find(n => n.posX === node.posX && n.posY === node.posY);

        if (!hasNode) {
            this.removeFromCloseList(node);
            this.openList.push(node);
        }
    }

    getCurrentNode() {
        let minF = 1000000;
        let currentNode = null;
        this.openList.forEach(node => {
            if (node.f < minF) {
                minF = node.f;
                currentNode = node;
            }
        });

        return currentNode;
    }

    getNeighbours(node, nodes) {
        const neighbours = [];

        if (this.getNode(node.posX, node.posY - 1, nodes)) {
            neighbours.push(this.getNode(node.posX, node.posY - 1, nodes));
        }

        if (this.getNode(node.posX, node.posY + 1, nodes)) {
            neighbours.push(this.getNode(node.posX, node.posY + 1, nodes));
        }

        if (this.getNode(node.posX - 1, node.posY, nodes)) {
            neighbours.push(this.getNode(node.posX - 1, node.posY, nodes));
        }

        if (this.getNode(node.posX + 1, node.posY, nodes)) {
            neighbours.push(this.getNode(node.posX + 1, node.posY, nodes));
        }

        return neighbours;
    }

    getNode(posX, posY, nodes) {
        return nodes.find(node => node.posX === posX && node.posY === posY);
    }

    isOnCloseList(node) {
        return this.closeList.some(n => n.posX === node.posX && n.posY === node.posY);
    }

    isOnOpenList(node) {
        return this.openList.some(n => n.posX === node.posX && n.posY === node.posY);
    }
}

import { Model } from './../model';
import { Collection } from './collection';

export class Renderer extends Collection {
    /**
     * @param {Array<Model>|Model} models
     * @param {Callable} [order=function()]
     */
    add(models, order = () => 0) {
        if (!Array.isArray(models)) {
            models = [models];
        }

        if (typeof order !== 'function') {
            order = () => order;
        }

        models.forEach((model, index) => {
            if (!model instanceof Model) {
                throw new Error(`Parameter from models[${index}] has to be an instance of Model, it's an instance of ${typeof model} instead.`);
            }

            this.push({ model, order });
        });
    }

    /**
     * @return {Array<Model>}
     */
    sortModels() {
        return this.sort((a, b) => {
            const orderA = a.order(a.model);
            const orderB = b.order(b.model);

            if (orderA < orderB) {
                return -1;
            } else if (orderA > orderB) {
                return 1;
            }

            return 0;
        });
    }

    /**
     * @param {Callable} callback
     */
    remove(callback) {
        const index = this.items.findIndex(item => callback(item.model));

        if (index > -1) {
            return this.items.splice(index, 1);
        }

        return false;
    }

    /**
     * @param {Drawer} drawer
     */
    render(drawer) {
        if (!drawer instanceof Drawer) {
            throw new Error(`Parameter drawer has to be an instance of Drawer, it's an instance of ${typeof drawer} instead.`);
        }

        this.sortModels();
        this.forEach(el => {
            if (!!el.model.render) {
                drawer.drawModel(el.model);
            }
        });
    }
}

import { Application } from './application';
