import { EventEmitter } from './event-emitter';

/**
 * Base class for every model entity
 * @example
 * let model = new Model(10, 10, 100, 200);
 */
export class Model extends EventEmitter {

    /**
     * @param {number} x
     * @param {number} y
     * @param {number} width
     * @param {number} height
     * @param {Object} [hitbox]
     * @param {number} [hitbox.x]
     * @param {number} [hitbox.y]
     * @param {number} [hitbox.width]
     * @param {number} [hitbox.height]
     */
    constructor(x, y, width, height, hitbox = {}) {
        super();

        /** @type {number} */
        this.x = x;
        /** @type {number} */
        this.y = y;
        /** @type {number} */
        this.width = width;
        /** @type {number} */
        this.height = height;

        this.hitbox = hitbox;
        this.collision = false;
        this.parent = {};
    }

    /**
     * @return {Object}
     * @property {number} x
     * @property {number} y
     * @property {number} width
     * @property {number} height
     */
    get hitbox() {
        return {
            x: this.x + this._hitbox.x,
            y: this.y + this._hitbox.y,
            width: this._hitbox.width,
            height: this._hitbox.height,
            radius: this._hitbox.radius
        }
    }

    /**
     * @param {Object} [hitbox]
     * @param {Object} [hitbox.x]
     * @param {Object} [hitbox.y]
     * @param {Object} [hitbox.width]
     * @param {Object} [hitbox.height]
     */
    set hitbox(hitbox) {
        this._hitbox = {
            x: hitbox.x || 0,
            y: hitbox.y || 0,
            width: hitbox.width || this.width,
            height: hitbox.height || this.height,
            radius: hitbox.radius || null
        }
    }

    /**
     * @property {number} value
     */
    set x(value) {
        this._x = Math.round(value);
    }

    /**
     * @return {number}
     */
    get x() {
        return this._x;
    }

    /**
     * @property {number} value
     */
    set y(value) {
        this._y = Math.round(value);
    }

    /**
     * @return {number}
     */
    get y() {
        return this._y;
    }

    /**
     * @param {number} dt
     */
    step(dt) {}

    /**
     * @param {RenderingContext} ctx
     * @param {Drawer} [drawer=null]
     */
    render(ctx, drawer = null) {}

    /**
     * @return {Object}
     * @property {number} x
     * @property {number} y
     * @property {number} width
     * @property {number} height
     * @property {Object} hitbox
     * @property {boolean} collision
     */
    serialize() {
        return {
            x: this.x,
            y: this.y,
            width: this.width,
            height: this.height,
            hitbox: this._hitbox,
            collision: this.collision
        };
    }

    /**
     * @param {Object} data
     * @param {number} data.x
     * @param {number} data.y
     * @param {number} data.width
     * @param {number} data.height
     * @param {Object} data.hitbox
     * @param {boolean} data.collision
     * @return {Model}
     */
    static deserialize({ x, y, width, height, hitbox, collision }) {
        const model = new Model(x, y, width, height, hitbox);

        model.collision = collision;

        return model;
    }
}

import { Model } from './model';
import { Container } from './container';

import io from './io';
import keys from './keys';

/**
 * Mouse service
 * @example
 * let tile = { x: 10, y: 10, width: 32, height: 32 };
 * if (mouse.isOver(tile)) {
 *     tile.backgroundColor = 'red';
 * }
 */
export class Mouse {
    constructor() {
        /** @type {number} */
        this.x = 0;

        /** @type {number} */
        this.y = 0;

        /** @type {number} - absolute x position */
        this.ax = 0;

        /** @type {number} - absolute y postion */
        this.ay = 0;

        /** @type {number} */
        this.scaleX = 1;

        /** @type {number} */
        this.scaleY = 1;
    }

    /**
     * Check if mouse coordinates has collisions with object
     * @protected
     * @param {Object} object
     * @param {number} object.x
     * @param {number} object.y
     * @param {number} object.width
     * @param {number} object.height
     */
    hasCollision(object) {
        if (object.x === undefined || object.y === undefined || object.width === undefined || object.height === undefined) {
            throw new Error(`Object ${object} is not valid, needs x, y, width and height parameters.`);
        }

        const x = this.ax > this.x ? this.ax : this.x;
        const y = this.ay > this.y ? this.ay : this.y;

        if (x / this.scaleX <= object.x + object.width &&
            object.x <= x / this.scaleX &&
            y / this.scaleY <= object.y + object.height &&
            object.y <= y / this.scaleY
        ) {
            return true;
        }

        return false;
    }

    /**
     * Check if mouse is over object
     * @param {Object} object
     * @param {number} object.x
     * @param {number} object.y
     * @param {number} object.width
     * @param {number} object.height
     * @return {boolean}
     */
    isOver(object) {
        return this.hasCollision(object);
    }

    /**
     * Check if mouse is out of object
     * @param {Object} object
     * @param {number} object.x
     * @param {number} object.y
     * @param {number} object.width
     * @param {number} object.height
     * @return {boolean}
     */
    isOut(object) {
        return !this.hasCollision(object);
    }

    /**
     * Check if mouse click and is over object
     * @param {Object} object
     * @param {number} object.x
     * @param {number} object.y
     * @param {number} object.width
     * @param {number} object.height
     * @return {boolean}
     */
    isClickOn(object) {
        return io[keys.LEFT_CLICK] && this.isOver(object);
    }

    /**
     * Check if mouse click and is out of object
     * @param {Object} object
     * @param {number} object.x
     * @param {number} object.y
     * @param {number} object.width
     * @param {number} object.height
     * @return {boolean}
     */
    isClickOut(object) {
        return io[keys.LEFT_CLICK] && this.isOut(object);
    }

    /**
     * @param {Object} target
     * @param {number} target.x
     * @param {number} target.y
     * @param {number} target.width
     * @return {number}
     */
    getAngle(object) {
        if (object.x === undefined || object.y === undefined || object.width === undefined || object.height === undefined) {
            throw new Error(`Object ${object} is not valid, needs x, y, width and height parameters.`);
        }

        const x = this.ax > this.x ? this.ax : this.x;
        const y = this.ay > this.y ? this.ay : this.y;

        const rad = Math.atan2((y / this.scaleY)- (object.y + object.height / this.scaleY), (x / this.scaleX) - (object.x + object.width / this.scaleX));

        return rad * 180 / Math.PI;
    }
}

const mouse = new Mouse();
export default mouse;

