import { Model } from './model';

// @TODO : doc
export class Particle extends Model {

    constructor(x, y, type, options) {
        super(x, y);

        this.type = type;
        this.options = options;
        this.opacity = 1;
    }

    step(dt) {
        if (this.opacity > 0) {
            this.opacity -= this.options.step || 0.01;
            this.opacity = this.opacity < 0 ? 0 : this.opacity;

            this.x += Math.cos(this.options.rotate || 0) * this.options.velocity || 0;
            this.y += Math.sin(this.options.rotate || Math.PI / 2) * this.options.velocity || 0;
        }
    }

    render(ctx, drawer) {
        if (this.opacity <= 0 || null === drawer) {
            return;
        }

        drawer.save();
        drawer.ctx.globalAlpha = this.opacity;

        switch (this.type) {
            case 'text':
                drawer.drawText(
                    this.options.text,
                    this.x,
                    this.y,
                    this.options.size || '8pt',
                    this.options.font || 'Arial, sans-serif',
                    this.options.color || 'black',
                    this.options.style || '',
                    this.options.align || 'left'
                );
                break;
            case 'image':
                if (!!this.options.image) {
                    drawer.drawImage(this.options.image, this.x, this.y);
                }
                break;
            case 'rect':
            case 'rectangle':
                if (!!this.options.width && !!this.options.height) {
                    drawer.drawRect(
                        this.x,
                        this.y,
                        this.options.width,
                        this.options.height,
                        this.options.size,
                        this.options.color
                    );
                }
                break;
            case 'fillrect':
            case 'fillrectangle':
            case 'fill-rectangle':
                if (!!this.options.width && !!this.options.height) {
                    drawer.drawFillRect(
                        this.x,
                        this.y,
                        this.options.width,
                        this.options.height,
                        this.options.color,
                        this.options.lineSize,
                        this.options.lineColor
                    );
                }
                break;
            case 'circ':
            case 'circle':
                if (!!this.options.radius) {
                    drawer.drawCircle(
                        this.x,
                        this.y,
                        this.options.radius,
                        this.options.size,
                        this.options.color,
                        this.options.start,
                        this.options.end
                    );
                }
                break;
            case 'fillcirc':
            case 'fillcircle':
            case 'fill-circle':
                if (!!this.options.radius) {
                    drawer.drawFillCircle(
                        this.x,
                        this.y,
                        this.options.radius,
                        this.options.color,
                        this.options.lineSize,
                        this.options.lineColor,
                        this.options.start,
                        this.options.end
                    );
                }
                break;
        }
        drawer.restore();

        if (!!this.options.rotate) {
            drawer.rotateModel(
                this,
                this.options.rotate,
                this.options.pivotX,
                this.options.pivotY
            );
        }
    }

    serialize() {
        return Object.assign(super.serialize(), {
            type: this.type,
            options: this.options,
            opacity: this.opacity
        });
    }

    static deserialize({
        x,
        y,
        type,
        options,
        opacity
    }) {
        const particle = new Particle(x, y, type, options);

        particle.opacity = opacity;

        return particle;
    }
}

/**
 * @ignore
 */
class CollisionChecker {

    constructor () {
        this.pair = [];
    }

    add (model, platforms, event) {

        if (!Array.isArray(platforms)) {
            platforms = [platforms];
        }

        this.pair.push({ model, platforms, event });
    }

    check (td) {
        let model, hasCollision;
        this.pair.forEach(pair => {
            model = Object.create(pair.model);
            model.step(td);
            hasCollision = this.hasCollisions(this.getHitbox(model), pair.platforms.map(el => this.getHitbox(el)));

            if (!!hasCollision) {
                pair.event(pair.model);
            }
        });
    }

    hasCollision (hitbox, hitboxes) {

        if (!Array.isArray(hitboxes)) {
            hitboxes = [hitboxes];
        }

        let rect;
        for (let i = 0; i < hitboxes.length; i++) {
            rect = hitboxes[i];

            if (hitbox.x < rect.x + rect.width &&
                hitbox.x + hitbox.width > rect.x &&
                hitbox.y < rect.y + rect.height &&
                hitbox.y + hitbox.height > rect.y
            ) {
                return true;
            }
        }

        return false;
    }

    getHitbox (model) {
        if (!!model.hitbox) {
            return {
                x: model.x + model.hitbox.x,
                y: model.y + model.hitbox.y,
                width: model.hitbox.width,
                height: model.hitbox.height
            }
        }

        return {
            x: model.x,
            y: model.y,
            width: model.width,
            height: model.height
        }
    }

    clear () {
        this.pair = [];
    }
}

module.exports = CollisionChecker;

